<?phpdefined('BASEPATH') OR exit('No direct script access allowed'); class Site extends MX_Controller {		public function __construct(){		parent::__construct();		$this->load->model('model_pengguna');	}		public function index(){		$this->page->template('login_tpl');		$this->page->view();	}		public function login(){		$username = $this->input->post('username');		$pass 	  = $this->input->post('password');		$password = MD5($pass);		$data = array(		'username' => $username,		'password' => $password,		);		$cek = $this->db->get_where('pengguna', $data);		$cek1 = $cek->row_array();		if ($cek1['blokir'] == 'Y' ) {			$this->session->set_flashdata('status', 'failed');			redirect(base_url());				}else{			if ($cek->num_rows() == 0) {				$this->session->set_flashdata('status', 'failed');				redirect(base_url());			}			else {				$pengguna = $cek->row();				$this->session->set_userdata('pengguna', $pengguna);								redirect(site_url('/dashboard'));			}  		}	}			public function logout(){		$this->session->sess_destroy();		redirect(site_url('/site'));	}	}/* End of file site.php *//* Location: ./application/controllers/site.php */