<?php defined('BASEPATH') OR exit('No direct script access allowed'); class pendaftaran extends MX_Controller {	 	 	public function __construct(){		parent::__construct();		 		$this->page->use_directory(); 		$this->load->model('model_pendaftaran');		}	public function index() {			$orang = $this->session->userdata('pengguna')->id; 		if($orang == 11){					$this->page->view('pendaftaran_index', array(				'add'			=> $this->page->base_url('/add'),				'grid'			=> $this->model_pendaftaran->get_pendaftaran(),			));		}else{			$pengguna = $this->session->userdata('pengguna')->id;						$this->page->view('pendaftaran_index', array(				'add'			=> $this->page->base_url('/add'),				'grid'			=> $this->model_pendaftaran->get_pendaftaran_by_pengguna($pengguna),			));		}		}	private function form($action = 'insert', $id_pendaftaran = ''){		$this->page->view('pendaftaran_form', array (			'back'			=> $this->agent->referrer(),			'action'		=> $this->page->base_url("/{$action}/{$id_pendaftaran}"),			'pendaftaran'	=> $this->model_pendaftaran->by_id_pendaftaran($id_pendaftaran),		));	}		public function edit($id_pendaftaran){		$this->form('update', $id_pendaftaran);	}		public function update($id){		$data = array(			'anak_ke' => $this->input->post('anak_ke'),			'jum_saudara' => $this->input->post('jum_saudara'),			'status_ortuwali' => $this->input->post('status_ortuwali')					);		$this->db->where('id_pendaftaran', $id); 		$this->db->update('pendaftaran', $data);				redirect($this->page->base_url());	}	public function add(){		$this->form();	}	public function insert(){		$anak_id = $this->input->post('anak_id');		$data = array (			'anak_id' => $anak_id		);		date_default_timezone_set("Asia/Bangkok");		$date = date_create();		$date2 = date_format($date,"d F Y");		$cek = $this->db->get_where('pendaftaran', $data);		$cek1 = $cek->row_array();				if (isset($cek1)) {			$this->session->set_flashdata('status', 'Pendaftaran gagal, anak Anda telah terdaftar');			redirect($this->agent->referrer());		}		else{			$data = array(				'anak_id' => $this->input->post('anak_id'),				'anak_ke' => $this->input->post('anak_ke'),				'jum_saudara' => $this->input->post('jum_saudara'),				'ayah_id' => $this->input->post('ayah_id'),				'ibu_id' => $this->input->post('ibu_id'),				'status_anak' => $this->input->post('status_anak'),				'status_ortuwali' => $this->input->post('status_ortuwali'),				'tgl_pendaftaran' => $date2,			);			$this->db->insert('pendaftaran', $data);						redirect($this->page->base_url());		}	}	public function delete($id_pendaftaran){		$this->db->delete('pendaftaran', array('id_pendaftaran' => $id_pendaftaran));		redirect($this->agent->referrer());	}		public function options_pendaftaran($id){		$jabatan = $this->db->order_by('nama')->get('jabatan');		return options($pendaftaran, 'id', $id, 'nama');	}		public function options_status_anak($id){		$jabatan = $this->db->order_by('nama')->get('jabatan');		return options($pendaftaran, 'id', $id, 'nama');	}	public function go_ortu(){		$id_pendaftaran =  $this->input->post('id_pendaftaran');		$data = $this->model_pendaftaran->by_id_pendaftaran($id_pendaftaran);		$daftar = array(			'id_pendaftaran'	=> $data->id_pendaftaran,			'nama_lengkap_anak'	=> $data->nama_lengkap_anak		);		$this->session->set_userdata('daftar', $daftar);		redirect(site_url('/ortu/prenatal/'));	}	}/* End of file pendaftaran.php *//* Location: ./application/modules/pendaftaran/controllers/pendaftaran.php */